import React, { Component } from "react";
import { Link, animateScroll as scroll } from "react-scroll"
import logo from "../logo.svg";

export default class Navbar extends Component {

  scrollToTop = () => {
    scroll.scrollToTop();
  }
  render() {
    return (
      <nav className="nav" id="navbar">
        <div className="nav-content">
          <img
            src={logo}
            className="nav-logo"
            alt="Logo."
            onClick={this.scrollToTop}
          />
          <ul className="nav-items">
            <Link className="nav-item"
              activeClass="active"
              to="section1"
              spy={true}
              smooth={true}
              offset={-70}
              duration={250}
            >Section 1</Link>
            <Link className="nav-item"
              activeClass="active"
              to="section2"
              spy={true}
              smooth={true}
              offset={-70}
              duration={250}
            >Section 2</Link>
            <Link className="nav-item"
              activeClass="active"
              to="section3"
              spy={true}
              smooth={true}
              offset={-70}
              duration={250}
            >Section 3</Link>
            <Link className="nav-item"
              activeClass="active"
              to="section4"
              spy={true}
              smooth={true}
              offset={-70}
              duration={250}
            >Section 4</Link>
            <Link className="nav-item"
              activeClass="active"
              to="section5"
              spy={true}
              smooth={true}
              offset={-70}
              duration={250}
            >Section 5</Link>
          </ul>
        </div>
      </nav>
    );
  }
}
